import React from "react";
import Sidebar from "../../components/sidebar/Sidebar";
import Styles from "./Dashboard.module.scss"; // SCSS module

const Dashboard = () => {
  // Sample Hotel Data (Replace with API Data)
  const hotel = {
    name: "Grand Palace Hotel",
    email: "info@grandpalace.com",
    address: "123, Beach Road, California",
    roomCount: 120,
    revenue: "$150K",
    totalBookings: 450,
    guides: 20,
    photo: "data:image/jpeg;base64,", // Assuming the image is stored in public/images/
  };

  // Sample Package Data
  const packages = [
    { id: 1, image: "data:image/jpeg;base64,", title: "Luxury Spa Retreat" },
    { id: 2, image: "data:image/jpeg;base64,", title: "Beachside Getaway" },
  ];

  // Function to Display Hotel Profile
  const renderHotelProfile = () => (
    <div className={Styles.profileHeader}>
      <img src={hotel.photo} alt="Hotel" className={Styles.profilePic} />
      <div className={Styles.hotelInfo}>
        <h2>{hotel.name}</h2>
        <p>{hotel.email}</p>
        <p>{hotel.address}</p>
      </div>
    </div>
  );

  // Function to Display Stats (Total Bookings, Revenue, etc.)
  const renderStats = () => (
    <div className={Styles.statsSection}>
      {[{ value: hotel.totalBookings, label: "Bookings" },
        { value: hotel.roomCount, label: "Rooms" },
        { value: hotel.guides, label: "Guides" },
        { value: hotel.revenue, label: "Revenue" }]
        .map((stat, index) => (
          <div key={index} className={Styles.stat}>
            <h3>{stat.value}</h3>
            <p>{stat.label}</p>
          </div>
        ))}
    </div>
  );

  // Function to Display Packages
  const renderPackages = () => (
    <div className={Styles.packageGrid}>
      {packages.map((pkg) => (
        <div key={pkg.id} className={Styles.packageCard}>
          <img src={pkg.image} alt={pkg.title} className={Styles.packageImg} />
          <p>{pkg.title}</p>
        </div>
      ))}
    </div>
  );

  return (
    <div className={Styles.dashboardContainer}>
      <Sidebar />
      <div className={Styles.profileSection}>
        {renderHotelProfile()}
        {renderStats()}
        {renderPackages()}
      </div>
    </div>
  );
};

export default Dashboard;
